/* ----------- blinking ----------------------------------------- */

	var blinkElements = new Array();
	
	function doBlink()
	{
		for( x in blinkElements )
		{
			if( blinkElements[x].style )
			{
				blinkElements[x].style.visibility = blinkElements[x].style.visibility == "" ? "hidden" : "";
			}
		}
	
		setTimeout(doBlink, 1000);
	}
	
	function addBlink(element)
